/// <reference lib="webworker" />
import { precacheAndRoute, cleanupOutdatedCaches, createHandlerBoundToURL } from 'workbox-precaching';
import { registerRoute, NavigationRoute } from 'workbox-routing';
import { NetworkFirst, StaleWhileRevalidate } from 'workbox-strategies';
import { ExpirationPlugin } from 'workbox-expiration';
import { CacheableResponsePlugin } from 'workbox-cacheable-response';

declare let self: ServiceWorkerGlobalScope;

// Clean up old caches
cleanupOutdatedCaches();

// Precache all assets generated by the build
try {
  let manifest = self.__WB_MANIFEST;
  if (!manifest) {
    manifest = [];
  }
  precacheAndRoute(manifest);
} catch (e) {
  console.error("SW: Precache error", e);
}

// Cache static API data (roles, images, plans) with StaleWhileRevalidate
// These change infrequently, so we show cached version immediately and update in background
registerRoute(
  ({ url }) => 
    url.pathname === '/api/roles' || 
    url.pathname === '/api/images' || 
    url.pathname === '/api/billing/plans',
  new StaleWhileRevalidate({
    cacheName: 'api-static-data',
    plugins: [
      new CacheableResponsePlugin({ statuses: [0, 200] }),
      new ExpirationPlugin({ maxEntries: 20, maxAgeSeconds: 60 * 60 * 24 }), // 24 hours
    ],
  })
);

// Network-first for other API calls (don't cache WebSocket or real-time data)
registerRoute(
  ({ url }) => url.pathname.startsWith('/api/') && !url.pathname.includes('/ws'),
  new NetworkFirst({
    cacheName: 'api-cache',
    networkTimeoutSeconds: 10,
    plugins: [
      new CacheableResponsePlugin({ statuses: [0, 200] }),
      new ExpirationPlugin({ maxEntries: 100, maxAgeSeconds: 60 * 5 }),
    ],
  })
);

// Handle navigation requests - serve index.html for SPA routing
// This uses the precached index.html for all navigation requests (App Shell model)
const handler = createHandlerBoundToURL('index.html');
registerRoute(
  new NavigationRoute(handler, {
    denylist: [/^\/api/, /^\/ws/, /^\/r\//],
  })
);

// Skip waiting only when explicitly requested (e.g. by user clicking 'Update')
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});

self.addEventListener('activate', (event) => {
  event.waitUntil(self.clients.claim());
});
